        private void loadDsDthuoc(int mabn)
        {
            var tongdt = (from a in DaTaContext.DThuocs.Where(p => p.MaBNhan == mabn && (_idDB == 0 || p.IDDienBien == _idDB)).Where(p => p.PLDV == 1)
                          join b in DaTaContext.DThuoccts on a.IDDon equals b.IDDon
                          join c in DaTaContext.DichVus.Where(p => (DungChung.Bien.MaBV == "01071" || DungChung.Bien.MaBV == "01049" || DungChung.Bien.MaBV == "12345" || DungChung.Bien.MaBV == "24297" || DungChung.Bien.MaBV == "56789") ? p.IDNhom != 7 : true) on b.MaDV equals c.MaDV
                          select new { a, b }).ToList();
            var query = (from c in tongdt
                         group c by new { c.a } into kq
                         select new { kq.Key.a.IDDon, kq.Key.a.NgayKe, kq.Key.a.KieuDon, kq.Key.a.MaBNhan, ChuaLinh = string.Join(";", kq.Where(p => p.b.Status == 0 && p.b.SoPL >= 0).Select(p => p.b.SoPL).ToList().Distinct()), DaLinh = string.Join(";", kq.Where(p => p.b.Status == 1 && p.b.SoPL >= 0).Select(p => p.b.SoPL).ToList().Distinct()) }).OrderByDescending(p => p.IDDon).ToList();

            _lDonThuoc = (from a in tongdt select a.a).OrderByDescending(p => p.IDDon).ToList();

            grcDonThuocdt.DataSource = null;
            grcDonThuocct.DataSource = null;
            grcDonThuocdt.DataSource = query;
        }
