            QLBV_Database.QLBVEntities data = new QLBV_Database.QLBVEntities(DungChung.Bien.StrCon);
            changect++;
            double dongia1 = 0, soluong = 0;
            int maDV = 0;
            string donvi = "";
            int maKhoXuat = 0;
            if (e.Column.Name == "coltenDVct")
            {
                if (e.Value != null)
                    maDV = Convert.ToInt32(e.Value.ToString());
