        private void DsYLenh(int _maBenhNhan, DateTime _tuNgay, DateTime _denNgay)
        {
            grvHienThiChiTiet.DataSource = null;
            CT.Clear();
            DateTime ngayTu = DungChung.Ham.NgayTu(_tuNgay);
            DateTime ngayDen = DungChung.Ham.NgayDen(_denNgay);
            int? phanLoai = null;
            if (lupPhanloai.EditValue != null && lupPhanloai.EditValue != "")
            {
                phanLoai = Convert.ToInt32(lupPhanloai.EditValue);
            }
            else
                phanLoai = null;

            if (phanLoai == 1)
            {
                var Lt = (from bn in DaTaContext.BenhNhans.Where(o => o.MaBNhan == _maBenhNhan)
                          join dt in DaTaContext.DThuocs.Where(o => o.NgayKe >= ngayTu && o.NgayKe <= ngayDen) on bn.MaBNhan equals dt.MaBNhan
                          join dtct in DaTaContext.DThuoccts on dt.IDDon equals dtct.IDDon
                          join dv in DaTaContext.DichVus.Where(p => p.PLoai == 1) on dtct.MaDV equals dv.MaDV
                          group new { dtct } by new { dt.IDDon, dt.MaCB, dt.MaKP, dt.MaKXuat, dt.NgayKe } into kq
                          select new
                          {
                              kq.Key.IDDon,
                              KpChiDinh = kq.Key.MaKP,
                              KPThucHien = kq.Key.MaKXuat,
                              MaYlenh = kq.Key.NgayKe,
                              BSChiDinh = kq.Key.MaCB,
                              Status = (kq.Where(o => o.dtct.Status == 1).Count() == kq.Count())
                          }).ToList();

                var cbs = (from mb in Lt
                           join kpcd in DaTaContext.KPhongs on mb.KpChiDinh equals kpcd.MaKP
                           join kpth in DaTaContext.KPhongs on mb.KPThucHien equals kpth.MaKP into kpth1
                           from kp1 in kpth1.DefaultIfEmpty()
                           join cbcd in DaTaContext.CanBoes on mb.BSChiDinh equals cbcd.MaCB
                           select new
                           {
                               mb.IDDon,
                               NgayYlenh = mb.MaYlenh,
                               MaKpChiDinh = mb.KpChiDinh,
                               KpChiDinh = kpcd.TenKP,
                               MaKpThucHien = mb.KPThucHien,
                               KpThucHien = kp1 != null ? kp1.TenKP : "",
                               MaCBChiDinh = mb.BSChiDinh,
                               BSChiDinh = cbcd.TenCB,
                               mb.Status,
                           }).ToList();
                foreach (var item in cbs)
                {

                    ChiTietCLSYlenh ThemMoiYLenh = new ChiTietCLSYlenh();
                    ThemMoiYLenh.PLoai = 1;
                    ThemMoiYLenh.ID = item.IDDon;
                    ThemMoiYLenh.Status = item.Status;
                    ThemMoiYLenh.ngayylenh = item.NgayYlenh.HasValue ? item.NgayYlenh.Value.ToString("dd/MM/yyyy HH:mm:ss") : "";
                    ThemMoiYLenh.Kpchidinh = item.KpChiDinh;
                    ThemMoiYLenh.kpthuchien = item.KpThucHien;
                    ThemMoiYLenh.bschidinh = item.BSChiDinh;
                    ThemMoiYLenh.mkpcd = item.MaKpChiDinh;
                    ThemMoiYLenh.mkpth = item.MaKpThucHien;
                    ThemMoiYLenh.mcbcd = item.MaCBChiDinh;
                    string nyl = Convert.ToString(ThemMoiYLenh.ngayylenh.ToString());
                    string[] a = nyl.Split('/');
                    string ma = a[0].ToString() + a[1].ToString() + a[2].ToString();
                    string[] a1 = ma.Split(' ');
                    string ma2 = a1[0].ToString();
                    ThemMoiYLenh.maylenh = ma2;

                    CT.Add(ThemMoiYLenh);
                }
            }
            else if (phanLoai == 2)
            {
                var LT = (from bn in DaTaContext.BenhNhans.Where(o => o.MaBNhan == _maBenhNhan)
                          join cls in DaTaContext.CLS.Where(p => p.NgayThang >= ngayTu && p.NgayThang <= ngayDen) on bn.MaBNhan equals cls.MaBNhan
                          join cd in DaTaContext.ChiDinhs on cls.IdCLS equals cd.IdCLS
                          join dv in DaTaContext.DichVus.Where(p => p.PLoai == 2) on cd.MaDV equals dv.MaDV
                          select new
                          {
                              cls.IdCLS,
                              Status = (cd.Status == 1),
                              KpChiDinh = cls.MaKP,
                              KPThucHien = cls.MaKPth,
                              MaYlenh = cls.NgayThang,
                              BSChiDinh = cls.MaCB,
                              BSThucHien = cls.MaCBth,

                          }).ToList();
                var cbs = (from mb in LT
                           join kpcd in DaTaContext.KPhongs on mb.KpChiDinh equals kpcd.MaKP
                           join kpth in DaTaContext.KPhongs on mb.KPThucHien equals kpth.MaKP into kpth1
                           from kp1 in kpth1.DefaultIfEmpty()
                           join cbcd in DaTaContext.CanBoes on mb.BSChiDinh equals cbcd.MaCB
                           join cbth in DaTaContext.CanBoes on mb.BSThucHien equals cbth.MaCB into cbth1
                           from cb1 in cbth1.DefaultIfEmpty()
                           select new
                           {
                               mb.IdCLS,
                               mb.Status,
                               NgayYlenh = mb.MaYlenh,
                               KpChiDinh = kpcd.TenKP,
                               KpThucHien = kp1 != null ? kp1.TenKP : "",
                               BSChiDinh = cbcd.TenCB,
                               BSThucHien = cb1 != null ? cb1.TenCB : "",
                               makpcd = mb.KpChiDinh,
                               makpth = mb.KPThucHien,
                               mbscd = mb.BSChiDinh,
                               mbsth = mb.BSThucHien,

                           }).ToList();
                foreach (var item in cbs)
                {

                    ChiTietCLSYlenh ThemMoiCLS = new ChiTietCLSYlenh();
                    ThemMoiCLS.ID = item.IdCLS;
                    ThemMoiCLS.PLoai = 2;
                    ThemMoiCLS.Status = item.Status;
                    ThemMoiCLS.ngayylenh = item.NgayYlenh.HasValue ? item.NgayYlenh.Value.ToString("dd/MM/yyyy HH:mm:ss") : "";
                    ThemMoiCLS.Kpchidinh = item.KpChiDinh;
                    ThemMoiCLS.kpthuchien = item.KpThucHien;
                    ThemMoiCLS.bschidinh = item.BSChiDinh;
                    ThemMoiCLS.bsthuchien = item.BSThucHien;
                    ThemMoiCLS.mkpcd = item.makpcd;
                    ThemMoiCLS.mkpth = item.makpth;
                    ThemMoiCLS.mcbcd = item.mbscd;
                    ThemMoiCLS.mcbth = item.mbsth;
                    string nyl = Convert.ToString(ThemMoiCLS.ngayylenh.ToString());
                    string[] a = nyl.Split('/');
                    string ma = a[0].ToString() + a[1].ToString() + a[2].ToString();
                    string[] a1 = ma.Split(' ');
                    string maylenh = a1[0].ToString();
                    ThemMoiCLS.maylenh = maylenh;

                    CT.Add(ThemMoiCLS);
                }
            }

            var KetQua = (from k in CT
                          group k by new { k.ID, k.PLoai, NgayYLenh = k.ngayylenh, KPChiDinh = k.Kpchidinh, KpThucHien = k.kpthuchien, BacSiChiDinh = k.bschidinh, BacSiThucHien = k.bsthuchien, MaYLenh = k.maylenh, MaKpChiDinh = k.mkpcd, MaKpThucHien = k.mkpth, MaCanBoChiDinh = k.mcbcd, MaCanBoThucHien = k.mcbth, k.Status } into kq
                          select new ChiTietCLSYlenh
                          {
                              ID = kq.Key.ID,
                              PLoai = kq.Key.PLoai,
                              Status = kq.Key.Status,
                              ngayylenh = kq.Key.NgayYLenh,
                              Kpchidinh = kq.Key.KPChiDinh,
                              kpthuchien = kq.Key.KpThucHien,
                              bschidinh = kq.Key.BacSiChiDinh,
                              maylenh = kq.Key.MaYLenh,
                              bsthuchien = kq.Key.BacSiThucHien,
                              mkpcd = kq.Key.MaKpChiDinh,
                              mkpth = kq.Key.MaKpThucHien,
                              mcbcd = kq.Key.MaCanBoChiDinh,
                              mcbth = kq.Key.MaCanBoThucHien,
                          }).OrderByDescending(o => o.ngayylenh).ToList();

            grvYLenh.DataSource = KetQua;
        }

