                              select new DienBienYHCT
                              {
                                  ThucHienYL = string.Join("\n", kq.Select(p => (p.ThucHienYL != "" && p.ThucHienYL != null) ? (p.ThucHienYL) : "").ToArray()),
                                  HuongDtri = string.Join("\n", kq.Select(p => (p.HuongDtri != "" && p.HuongDtri != null) ? (p.HuongDtri) : "").ToArray()),
