                                  YLenh = (DungChung.Bien.MaBV == "14018" ? "Đơn duyệt ngày " + Environment.NewLine + ngayGop + Environment.NewLine : "") + string.Join("\n", kq.Select(p => (p.YLenh != "" && p.YLenh != null) ? ("\n" + p.YLenh) : "").ToArray()),
