                            item.DienBien1 += "<p align=right valign=bottom><b>" + item.TenCB + "</b></p>";
                            item.YLenh += "<p align=right valign=bottom><b>" + item.TenCB + "</b></p>";
                        }

                        item.YLenh = item.YLenh.Replace(Environment.NewLine, "<br />");
                        item.DienBien1 = item.DienBien1.Replace(Environment.NewLine, "<br />");
                        item.Count = qcount.Single(p => p.NgayNhap == item.NgayNhap).Count;
                        item.NgayNhapYHCT14018 = ngayGop;
                    }

                    listDBYHCT.AddRange(q4);
                }

                var ttbn = (from bn in DaTaContext.BenhNhans.Where(p => p.MaBNhan == _int_maBN)
                            join bnkb in DaTaContext.BNKBs on bn.MaBNhan equals bnkb.MaBNhan
                            join kp in DaTaContext.KPhongs on bnkb.MaKP equals kp.MaKP
                            select new { bn.TenBNhan, bn.Tuoi, bn.GTinh, bn.DChi, bnkb.BenhKhac, kp.TenKP, bnkb.Buong, bnkb.Giuong, bnkb.ChanDoan, bnkb.MaICD, bnkb.IDKB }).OrderByDescending(p => p.IDKB).ToList();
                if (ttbn.Count > 0)
                {
                    dic["TenBNhan"] = ttbn.First().TenBNhan.ToUpper();
                    dic["Tuoi"] = DungChung.Bien.MaBV == "14018" ? DungChung.Ham.TinhTuoiBenhNhan(DaTaContext, _int_maBN) : ttbn.First().Tuoi.ToString();
