                dic["ToDieuTriYHCT_14018"] = isToDieuTriYHCT_14018;
                // if (dienbien.Count > 0)
                var kphongDT = DaTaContext.KPhongs.FirstOrDefault(o => o.MaKP == _makp);
                if (kphongDT != null)
                    dic["KhoaDT"] = kphongDT.TenKP;

                var cbo = DaTaContext.CanBoes.FirstOrDefault(o => o.MaCB == DungChung.Bien.MaCB);
                if (cbo != null)
                    dic["TenCB"] = cbo.CapBac + "." + cbo.TenCB;
                dic["TongKet"] = _thoigianDT.TongKet;
                dic["TenCQ"] = DungChung.Bien.TenCQ.ToUpper();
                dic["TenCQCQ"] = DungChung.Bien.TenCQCQ.ToUpper();

                DungChung.Ham.Print(DungChung.PrintConfig.rep_ToDieuTri_34019, listDBYHCT.OrderBy(p => p.NgayNhap).ToList(), dic, false);
                #endregion

                return true;
                //}
            }
            else
            {
                MessageBox.Show("Bạn chưa chọn bệnh nhân");
                result = false;
            }
            return result;
        }

