                        }
                    }
                    else
                    {
                        var krtrakb = DaTaContext.BNKBs.Where(p => p.MaBNhan == _int_maBN).Where(p => p.MaKP == _makp).ToList();
                        if (krtrakb.Count == 0)
                        {
                            MessageBox.Show("Bệnh nhân chưa có chẩn đoán");
                            return;
                        }
                    }
                    if (DungChung.Bien.MaBV == "34019" || DungChung.Bien.MaBV == "14018")
                    {
