                case "colNgayNhapdb":
                    {
                        if (e.Value != null && grvDienBien.IsNewItemRow(e.RowHandle))
                        {
                            var ngay = (DateTime)e.Value;
                            DateTime tungay = DungChung.Ham.NgayTu(ngay);
                            DateTime ngayden = DungChung.Ham.NgayDen(ngay);
                            bool kt = false;
                            for (int i = 0; i < grvDienBien.RowCount; i++)
                            {
                                if (grvDienBien.GetRowCellDisplayText(i, colNgayNhapdb) != null && grvDienBien.GetRowCellDisplayText(i, colNgayNhapdb) != "")
                                {
                                    DateTime ngaydb = Convert.ToDateTime(grvDienBien.GetRowCellDisplayText(i, colNgayNhapdb));
                                    if (ngaydb >= tungay && ngaydb <= ngayden)
                                    {
                                        kt = true;
                                        break;
                                    }
                                }
                            }
                            if (!kt)
                            {
                                //grvDienBien.SetRowCellValue(e.RowHandle, colNgayNhapdb, ngay);
                                if (DungChung.Bien.MaBV != "14018")
                                {
                                    string DBmau = "+Giờ khám: " + DateTime.Now.Hour + "h" + " \r\n+Triệu chứng: \r\n-Ý thức \r\n-Cảm xúc: \r\n-Tri thức \r\n-Tư duy \r\n+Nội khoa: \r\n-Tim mạch \r\n-Hô hấp \r\n-Khác \r\n+Mạch:	N.độ: \r\n+HA:	Cân nặng:";
