        public void LoadYLenh(int _idDB)
        {
            string ylenhLoad = "";

            #region CLS
            QLBV_Database.QLBVEntities data = new QLBV_Database.QLBVEntities(DungChung.Bien.StrCon);
            List<FRM_chidinh_Moi.MyObject> _lall = new List<FRM_chidinh_Moi.MyObject>();
            List<FRM_chidinh_Moi.MyObject> qcd = (from cd in data.ChiDinhs
                                                  join cls in data.CLS.Where(o => o.IDDienBien == _idDB) on cd.IdCLS equals cls.IdCLS
                                                  join clsct in data.CLScts on cd.IDCD equals clsct.IDCD
                                                  join dv in data.DichVus on cd.MaDV equals dv.MaDV
                                                  // join dvct in data.DichVucts on clsct.MaDVct equals dvct.MaDVct
                                                  join tn in data.TieuNhomDVs on dv.IdTieuNhom equals tn.IdTieuNhom
                                                  join n in data.NhomDVs on tn.IDNhom equals n.IDNhom
                                                  select new FRM_chidinh_Moi.MyObject { IDNhom = tn.IDNhom ?? 0, TenNhom = n.TenNhom, TenTN = tn.TenRG, TenRG = dv.TenDV, TenDV = dv.TenDV, YLenh = cd.ChiDinh1, MaQD = dv.MaQD, ViTri = cd.YLenh2 }).ToList();
            var qcdExe = (from q in qcd
                          select new FRM_chidinh_Moi.MyObject { IDNhom = q.IDNhom, MaQD = q.MaQD, TenNhom = q.TenNhom, TenRG = ((DungChung.Bien.MaBV == "14018" && !string.IsNullOrWhiteSpace(q.MaQD)) ? (string.Format("({0}) {1} {2}", q.MaQD, q.TenRG, q.YLenh) + ((DungChung.Bien.MaBV == "14018" && q.TenTN == "Điều trị y học cổ truyền") ? (!string.IsNullOrEmpty(q.ViTri) ? (Environment.NewLine + q.ViTri) : "") : "")) : (q.TenRG + " " + q.YLenh)), TenTN = q.TenTN, TenDV = q.TenDV }).ToList();

            _lall.AddRange(qcdExe);

            _lall = _lall.OrderBy(p => p.IDNhom).ThenBy(p => p.TenTN).ThenBy(p => p.TenDV).ToList();

            var q1 = (from dv in _lall group dv by dv.TenNhom into x select new { TenNhom = x.Key, TenRG = "+ " + x.Key + ": " + (DungChung.Bien.MaBV == "14018" ? Environment.NewLine : "") + string.Join((DungChung.Bien.MaBV == "14018" ? Environment.NewLine : ", "), x.Select(p => p.TenRG).ToList().Distinct()) + Environment.NewLine }).ToList();
            var clsString = "- CLS: " + Environment.NewLine + string.Join("", q1.Select(p => p.TenRG));
            #endregion

            #region Thuốc

            var thuocString = "- Thuốc:" + Environment.NewLine;

            var qtenRG = (from dt in data.DThuocs.Where(o => o.IDDienBien == _idDB && o.PLDV == 1)
                          join dtct in data.DThuoccts on dt.IDDon equals dtct.IDDon
                          join dv in data.DichVus on dtct.MaDV equals dv.MaDV
                          join tn in data.TieuNhomDVs.Where(o => (o.TenRG.Contains("Thuốc") || o.TenRG.Contains("thuốc"))) on dv.IdTieuNhom equals tn.IdTieuNhom
                          select new { dtct.MaKXuat, dv.MaDV, tn.TenRG, dv.DongY, TenTT05 = dv.TenRG, dv.HamLuong, dv.DuongD, dv.TenHC, dtct.SoLuong, dtct.GhiChu, dtct.DonVi, dtct.SoLan }).ToList();
            if (qtenRG != null && qtenRG.Count > 0)
            {
                Dictionary<int, int> dic = new Dictionary<int, int>();
                var gnht = qtenRG.Where(o => o.TenRG.Contains("Thuốc gây nghiện") || o.TenRG.Contains("Thuốc hướng tâm thần") || (DungChung.Bien.MaBV == "14018" && (o.TenRG.Contains("Kháng sinh") || o.TenRG.Contains("kháng sinh")))).Select(o => o.MaDV).Distinct().ToList();
                foreach (var item in gnht)
                {
                    dic.Add(item, 0);
                }

                foreach (var item in qtenRG)
                {
                    bool checkKhoNhaThuoc = false;
                    var khoXuat = data.KPhongs.FirstOrDefault(o => o.MaKP == item.MaKXuat);
                    if (khoXuat != null)
                    {
                        if (khoXuat.DChi != null && khoXuat.DChi.ToLower() == "nhà thuốc bệnh viện")
                        {
                            checkKhoNhaThuoc = true;
                        }
                        else
                            checkKhoNhaThuoc = false;
                    }
                    string hamluong = "";
                    if (DungChung.Bien.MaBV == "34019")
                    {
                        hamluong = (!string.IsNullOrWhiteSpace(item.TenHC) ? ("(" + item.TenHC + ") ") : "") + (!string.IsNullOrWhiteSpace(item.HamLuong) ? item.HamLuong : "");
                    }
                    else
                        hamluong = !string.IsNullOrWhiteSpace(item.HamLuong) ? ("(" + item.HamLuong + ")") : "";
                    if (item.TenRG.Contains("Thuốc gây nghiện") || item.TenRG.Contains("Thuốc hướng tâm thần") || (DungChung.Bien.MaBV == "14018" && (item.TenRG.Contains("Kháng sinh") || item.TenRG.Contains("kháng sinh"))))
                    {
                        int sl = 0;
                        if (dic.ContainsKey(item.MaDV))
                        {
                            sl = dic[item.MaDV] + 1;
                            dic[item.MaDV] = sl;
                        }
                        int _maBN = 0; int ot;
                        if (int.TryParse(txtMaBNhan.Text, out ot))
                        {
                            _maBN = Convert.ToInt32(txtMaBNhan.Text);
                        }
                        if (DungChung.Bien.MaBV == "34019")
                            thuocString += "- " + "<b><u>" + sl.ToString() + "</u></b>" + item.TenTT05 + hamluong + " x " + (Convert.ToInt32(item.SoLuong)).ToString("D2") + "  " + item.DonVi + "  " + item.DuongD + ((item.GhiChu != "" && item.GhiChu != null) ? (", " + item.GhiChu) : "") + (checkKhoNhaThuoc ? " (tự túc)" : "") + Environment.NewLine;
                        else if (DungChung.Bien.MaBV == "14018")
                            thuocString += "(" + sl.ToString() + ") " + item.TenTT05 + " x " + (Convert.ToInt32(item.SoLuong)).ToString("D2") + " " + item.DonVi + "( " + item.DuongD + " " + item.SoLan + " lần" + ((item.GhiChu != "" && item.GhiChu != null) ? (", " + item.GhiChu) : "") + " )" + Environment.NewLine;
                        else
                            thuocString += "- " + sl.ToString() + item.TenTT05 + hamluong + " x " + (Convert.ToInt32(item.SoLuong)).ToString("D2") + "  " + item.DonVi + "  " + item.DuongD + ((item.GhiChu != "" && item.GhiChu != null) ? (", " + item.GhiChu) : "") + (checkKhoNhaThuoc ? " (tự túc)" : "") + Environment.NewLine;

                    }
                    else
                    {
                        if (DungChung.Bien.MaBV == "14018")
                        {
                            thuocString += item.TenTT05 + " x " + (Convert.ToInt32(item.SoLuong)).ToString("D2") + " " + item.DonVi + "( " + item.DuongD + " " + item.SoLan + " lần" + ((item.GhiChu != "" && item.GhiChu != null) ? (", " + item.GhiChu) : "") + " )" + Environment.NewLine;
                        }
                        else
                        {
                            thuocString += "- " + item.TenTT05 + hamluong + " x " + (Convert.ToInt32(item.SoLuong)).ToString("D2") + "  " + item.DonVi + "  " + item.DuongD + ((item.GhiChu != "" && item.GhiChu != null) ? (", " + item.GhiChu) : "") + (checkKhoNhaThuoc ? " (tự túc)" : "") + Environment.NewLine;
                        }
                    }
                }
            }

            #endregion

            string vatTuString = "";
            #region Vật tư

            if (DungChung.Bien.MaBV == "14018")
            {
                var vatTuChuoi = "- Vật tư:" + Environment.NewLine;

                var qtenRGVT = (from dt in data.DThuocs.Where(o => o.IDDienBien == _idDB && o.PLDV == 1)
                                join dtct in data.DThuoccts on dt.IDDon equals dtct.IDDon
                                join dv in data.DichVus.Where(o => o.IDNhom == 10 || o.IDNhom == 11) on dtct.MaDV equals dv.MaDV
                                join tn in data.TieuNhomDVs on dv.IdTieuNhom equals tn.IdTieuNhom
                                select new { dtct.MaKXuat, dv.MaDV, tn.TenRG, dv.DongY, TenTT05 = dv.TenRG, dv.HamLuong, dv.DuongD, dv.TenHC, dtct.SoLuong, dtct.GhiChu, dtct.DonVi, dtct.SoLan, dv.TenDV }).ToList();
                if (qtenRGVT != null && qtenRGVT.Count > 0)
                {
                    foreach (var item in qtenRGVT)
                    {
                        vatTuChuoi += item.TenDV + " x " + (Convert.ToInt32(item.SoLuong)).ToString("D2") + " " + item.DonVi + Environment.NewLine;
                    }
                    vatTuString = vatTuChuoi;
                }
            }

            #endregion

            ylenhLoad = thuocString + vatTuString + clsString;
            txtYLenh_DB.Text = ylenhLoad;

            var qdienbien = DaTaContext.DienBiens.Where(p => p.ID == _idDB).FirstOrDefault();
            if (qdienbien != null)
            {
                qdienbien.YLenh = ylenhLoad;
                DaTaContext.SaveChanges();
            }
        }

