        private enum Loai
        {
            CDHA = 2,
            TDCN = 3,
        }

        private void LayKetQua(int idDB, DateTime ngayNhap, Loai loai)
        {
            var ngayTu = DungChung.Ham.NgayTu(ngayNhap);
            var ngayDen = DungChung.Ham.NgayDen(ngayNhap);
            string tenLoai = "";
            int iDNhom = 0;
            if (loai == Loai.CDHA)
            {
                tenLoai = "Chẩn đoán hình ảnh";
                iDNhom = 2;
            }
            else if (loai == Loai.TDCN)
            {
                tenLoai = "Thăm dò chức năng";
                iDNhom = 3;
            }

            listCLS = new List<ClsADO>();
            int rs;
            int _int_maBN = 0;
            if (Int32.TryParse(txtMaBNhan.Text, out rs))
                _int_maBN = Convert.ToInt32(txtMaBNhan.Text);
            if (DialogResult.Yes == MessageBox.Show(string.Format("Lấy kết quả {0} vào mục diễn biến bệnh?", tenLoai.ToLower()), "Hỏi lưu", MessageBoxButtons.YesNo, MessageBoxIcon.Question))
            {
                var xetnghiem1 = (from cls in DaTaContext.CLS.Where(p => p.MaBNhan == _int_maBN && p.NgayTH >= ngayTu && p.NgayTH < ngayDen)
                                  join cd in DaTaContext.ChiDinhs.Where(p => p.Status == 1) on cls.IdCLS equals cd.IdCLS
                                  join clsct in DaTaContext.CLScts on cd.IDCD equals clsct.IDCD
                                  join dv in DaTaContext.DichVus on cd.MaDV equals dv.MaDV
                                  join tn in DaTaContext.TieuNhomDVs.Where(p => p.IDNhom == iDNhom) on dv.IdTieuNhom equals tn.IdTieuNhom //p.TenRG.Contains("XN")
                                  select new { cd.MaCBth, dv.TenDV, cls.MaCB, tn.TenRG, cd.Status, cls.NgayTH, tn.IDNhom, cls.NgayThang, clsct.KetQua, TenDVct = "", TenDVctkq = "", cls.IdCLS, cd.KetLuan, clsct.MaDVct }).OrderBy(p => p.IdCLS).ToList();
                List<ClsADO> listClsADO = new List<ClsADO>();
                if (DungChung.Bien.MaBV == "34019")
                {
                    foreach (var item in xetnghiem1)
                    {
                        ClsADO ado = new ClsADO();
                        ado.Check = false;
                        ado.IdCLS = item.IdCLS;
                        ado.NgayTH = item.NgayTH;
                        ado.NgayThang = item.NgayThang;
                        ado.TenDV = item.TenDV;
                        int kq = 0;
                        if (item.TenRG == "Trắc nghiệm tâm lý")
                        {
                            if (int.TryParse(item.KetQua, out kq))
                            {
                                var kqct = (from k in DaTaContext.KQcts.Where(o => o.MaDVct == item.MaDVct && !(o.Tu == null && o.Den == null))
                                            select k).ToList();
                                if (kqct != null && kqct.Count > 0)
                                {
                                    foreach (var ct in kqct)
                                    {
                                        if (ct.Tu != null && ct.Den != null && ct.Tu <= kq && ct.Den >= kq)
                                        {
                                            ado.KetLuan = ct.KetLuan;
                                            break;
                                        }
                                        else if (ct.Tu != null && ct.Den == null && ct.Tu <= kq)
                                        {
                                            ado.KetLuan = ct.KetLuan;
                                            break;
                                        }
                                        else if (ct.Tu == null && ct.Den != null && ct.Den >= kq)
                                        {
                                            ado.KetLuan = ct.KetLuan;
                                            break;
                                        }
                                    }
                                }
                            }
                        }
                        else
                        {
                            ado.KetLuan = item.KetLuan;
                        }
                        listClsADO.Add(ado);
                    }
                    frmDSCLS_DienBien frmDSCLS = new frmDSCLS_DienBien(listClsADO, DSCLS);
                    frmDSCLS.ShowDialog();
                    if (listCLS.Count == 0)
                        return;
                    xetnghiem1 = (from xn in xetnghiem1.Where(o => listCLS.Select(p => p.IdCLS).Contains(o.IdCLS))
                                  join a in listClsADO on xn.IdCLS equals a.IdCLS
                                  select new { xn.MaCBth, xn.TenDV, xn.MaCB, xn.TenRG, xn.Status, xn.NgayTH, xn.IDNhom, xn.NgayThang, xn.KetQua, TenDVct = "", TenDVctkq = "", xn.IdCLS, a.KetLuan, xn.MaDVct }).ToList();
                }

                var q0 = (from xn in xetnghiem1
                          select new
                          {
                              xn.TenRG,
                              xn.TenDV,
                              xn.KetLuan,
                              xn.KetQua
                          }).ToList();

                //var q1 = (from a in q0
                //          group new { a } by new { a.TenRG } into kq
                //          select new
                //          {
                //              Ten = "  +" + kq.Key.TenRG + ":" + Environment.NewLine,
                //              kq,
                //          }).ToList();

                //string dienBien = string.Format("+{0}:", tenLoai) + Environment.NewLine;
                string dienBien = "";
                int count = 0;
                foreach (var item in q0)
                {
                    if (q0.Count > 1)
                    {
                        count++;
                        //dienBien += item.Ten;
                        //foreach (var subItem in item.kq)
                        //{
                        dienBien += "+" + item.TenDV + ": " + item.KetLuan + ((count >= 1 && count != q0.Count) ? Environment.NewLine : "");
                    }
                    else
                    {
                        dienBien += "+" + item.TenDV + ": " + item.KetLuan;
                    }

                    //}
                }

                var dbbenh = DaTaContext.DienBiens.FirstOrDefault(p => p.ID == idDB);
                if (dbbenh != null)
                {
                    dbbenh.DienBien1 += Environment.NewLine + dienBien;
                    DaTaContext.SaveChanges();
                }
            }
            grvBNhankb_FocusedRowChanged(null, null);
        }

