                    var id = int.Parse(idDienBien.ToString());
                    if (id > 0)
                    {
                        frmSaoNhieuNgayDienBien frm = new frmSaoNhieuNgayDienBien(id, RefreshChamSoc, 2, dtNgayKhamkb.DateTime);
                        frm.ShowDialog();
                    }
                }
            }
            catch (Exception ex)
            {
                DungChung.WriteLog.Warn(ex);
            }
            finally
            {
                rowHandle = -1;
            }
        }

